@OptIn(MapboxExperimental::class)
class MainActivity : ComponentActivity() {

    private lateinit var mainViewModel: MainViewModel

    @OptIn(ExperimentalPermissionsApi::class)
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)


        // Instantiate ViewModel
        mainViewModel = MainViewModel()

        // Load the bus positions from API
        mainViewModel.loadBusPositions()

        // LOad the alerts from API
        mainViewModel.loadAlerts()



        @SuppressLint("MissingPermission")

        fun SetLocation(mainViewModel: MainViewModel, activity: MainActivity) {

            // Get the fused location client
            val fusedLocationClient = LocationServices.getFusedLocationProviderClient(activity)

            // Get the last known location of the device
            if (ActivityCompat.checkSelfPermission(
                    this,
                    Manifest.permission.ACCESS_FINE_LOCATION
                ) != PackageManager.PERMISSION_GRANTED && ActivityCompat.checkSelfPermission(
                    this,
                    Manifest.permission.ACCESS_COARSE_LOCATION
                ) != PackageManager.PERMISSION_GRANTED
            ) {
                // TODO: Consider calling
                //    ActivityCompat#requestPermissions
                // here to request the missing permissions, and then overriding
                //   public void onRequestPermissionsResult(int requestCode, String[] permissions,
                //                                          int[] grantResults)
                // to handle the case where the user grants the permission. See the documentation
                // for ActivityCompat#requestPermissions for more details.
                return
            }
            fusedLocationClient.getLastLocation().addOnSuccessListener { location ->
                // Check if the location is not null
                if (location != null) {
                    // Create a point object from the location coordinates
                    val point = Point.fromLngLat(location.longitude, location.latitude)
                    // Use the loadLocation function to update the point state flow
                    mainViewModel.loadLocation(point)
                }
            }
        }

            @OptIn(ExperimentalPermissionsApi::class)
            @Composable
            fun GetPermission() {
                val permissionState =
                    rememberPermissionState(Manifest.permission.ACCESS_FINE_LOCATION)

                // Permission not granted, request permission
                if (!permissionState.status.isGranted) {
                    LaunchedEffect(permissionState) {
                        permissionState.launchPermissionRequest()
                    }
                } else {
                    // Permission granted, load user's location
                    SetLocation(mainViewModel, this@MainActivity)
                }
            }






























































































































































































































































































