    @OptIn(ExperimentalMaterial3Api::class)
    @Composable
    fun TransitApp() {
        val navController: NavHostController = rememberNavController()

        val mainViewModel = MainViewModel()

        mainViewModel.loadBusPositions()

        mainViewModel.loadAlerts()


        Scaffold(
            bottomBar = {
                BottomAppBar(
                    actions = {
                        IconButton(onClick = { navController.navigate("map") }) {
                            Icon(
                                painterResource(id = R.drawable.rounded_map_24),
                                contentDescription = "Map"
                            )
                        }
                        IconButton(onClick = { navController.navigate("route") }) {
                            Icon(
                                painterResource(id = R.drawable.rounded_directions_bus_24),
                                contentDescription = "Route"
                            )
                        }
                        IconButton(onClick = { navController.navigate("alert") }) {
                            Icon(
                                painterResource(id = R.drawable.baseline_bus_alert_24),
                                contentDescription = "Alert"
                            )
                        }
                    }
                )
            }
        )
        { innerPadding ->
            NavHost(
                navController = navController,
                startDestination = "map",
                modifier = Modifier.padding(innerPadding)
            ) {
                composable(route = "map") { MapScreen(mainViewModel) }
                composable(route = "route") { RouteScreen(mainViewModel) }
                composable(route = "alert") { AlertScreen(mainViewModel) }
            }
        }
    }



